import React, { useState } from 'react';
import SearchBar from './SearchBar';
import Result from './Result';
import './App.css';

function App() {
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = (query, site) => {
    // Mock search logic
    const results = [
      {
        title: 'Matrix',
        link: 'https://fmoviesz.to/movie/the-matrix-jv58',
        imgSrc: '/path/to/placeholder-image.png', // Replace with actual image path
      },
    ];
    setSearchResults(results);
  };

  return (
    <div className="app-container">
      <h1>Film & TV Extractor</h1>
      <SearchBar onSearch={handleSearch} />
      <div id="1-result-s">{searchResults.length} Result(s)</div>
      {searchResults.map((result, index) => (
        <Result key={index} result={result} />
      ))}
    </div>
  );
}

export default App;
